public final class CheckCode {

    private CheckCode() {
        super();
    }

    public static byte mathParity(byte[] headerBytes, byte[] bodyData) {
        byte result = headerBytes[2];
        for (int i = 3; i <= headerBytes.length - 1; i++) {
            result ^= headerBytes[i];
        }
        for (int i = 0; i <= bodyData.length - 1; i++) {
            result ^= bodyData[i];
        }
        return result;
    }

    public static void main(String[] args) {

        StringBuilder sb = new StringBuilder();

        sb.append("");

        byte[] headerBytes = ByteUtils.hexStringToBytes("23230201464d542d434539383736353433323132330101a2");
        byte[] body = ByteUtils.hexStringToBytes("");

        byte b = CheckCode.mathParity(headerBytes, body);


        System.out.println(ByteUtils.asHex(new byte[]{b}));

    }

}
